const Prices = ({ bpi }) => {
  const [currency, setCurrency] = useState("USD");

  return (
    <div>
      <ul className="list-group">
        <li className="list-group-item">
          Bitcoin rate for {bpi.USD.description}:{" "}
          <span className="badge badge-rpimary">{bpi.USD.code}</span>
        </li>
      </ul>
    </div>
  );
};

export default Prices;
